import * as React from 'react';
import { ScrollView, StyleSheet } from 'react-native';
import { Subheading, Avatar, Button } from 'react-native-paper';

import * as firebase from 'firebase';
import { DrawerContentComponentProps, DrawerContentOptions } from '@react-navigation/drawer';

export default function Profile({ navigation }: DrawerContentComponentProps<DrawerContentOptions>, user: firebase.User | null) {

  const thumbnail = user && user.photoURL ? require(user.photoURL) : null;

  return (
    <ScrollView style={styles.content} >
      {thumbnail ? (<Avatar.Image source={thumbnail} style={styles.image} />) : null}
      <Subheading>Hi{user?.displayName ? ' ' + user?.displayName : null}!</Subheading>
      <Button onPress={() => navigation.navigate('AuthFlow', {action: 'SIGN_OUT'})} style={styles.button}>
        Sign Out
      </Button>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  content: {
    flexDirection: 'column',
    paddingHorizontal: 16,
    paddingVertical: 12,
  },
  button: {
    margin: 8,
  },
  text: {
    textAlign: 'center',
    margin: 8,
  },
  image: {
    alignItems: 'center'
  }
});
