import * as React from 'react';
import { ScrollView, StyleSheet } from 'react-native';
import { Subheading, Avatar, Button } from 'react-native-paper';

import * as firebase from 'firebase';

export default function Profile(props: any) {
  const [user, setUser] = React.useState(firebase.auth().currentUser);

  React.useEffect(() => {
    const authenticationSubscriptionUnsubscribe = firebase.auth().onAuthStateChanged(userState => {
        setUser(userState);
      });

    return () => authenticationSubscriptionUnsubscribe()
  }, []);

  const thumbnail = user && user.photoURL ? require(user.photoURL) : null;

  console.log('user', user);
  return (
    <ScrollView style={styles.content} >
      {thumbnail ? (<Avatar.Image source={thumbnail} style={styles.image} />) : null}
      <Subheading>Hi {user?.displayName}!</Subheading>
      <Button  style={styles.button}>
        Sign Out
      </Button>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  content: {
    flexDirection: 'column',
    paddingHorizontal: 16,
    paddingVertical: 12,
  },
  button: {
    margin: 8,
  },
  text: {
    textAlign: 'center',
    margin: 8,
  },
  image: {
    alignItems: 'center'
  }
});
