import * as React from 'react';
import { View, TextInput, ActivityIndicator, StyleSheet, Text } from 'react-native';
import { Title, Button } from 'react-native-paper';
import { useTheme, ParamListBase } from '@react-navigation/native';
import {
  createStackNavigator,
  StackScreenProps,
} from '@react-navigation/stack';
import AuthContext from '../shared/AuthContext'

import * as firebase from 'firebase';

type AuthStackParams = {
  Splash: undefined;
  Home: undefined;
  SignIn: undefined;
  PostSignOut: undefined;
};

const SplashScreen = () => {
  return (
    <View style={styles.content}>
      <ActivityIndicator />
    </View>
  );
};

const SignInScreen = () => {
  const { signIn } = React.useContext(AuthContext);
  const { colors } = useTheme();
  const [email, onChangeEmail] = React.useState('Email');
  const [password, onChangePassword] = React.useState('Password');
  const [error, onChangeError] = React.useState('');

  return (
    <View style={styles.content}>
      <TextInput
        placeholder="Email"
        autoCompleteType="email"
        style={[
          styles.input,
          { backgroundColor: colors.card, color: colors.text },
        ]}
        onChangeText={onChangeEmail}
      />
      <TextInput
        placeholder="Password"
        secureTextEntry
        style={[
          styles.input,
          { backgroundColor: colors.card, color: colors.text },
        ]}
        onChangeText={onChangePassword}
      />
      {error !== undefined ? (<Text style={{color: 'red'}}>{error}</Text>) : null}
      <Button mode="contained" onPress={() => {
        signIn(email, password).then((errorMessage) => {
          if (errorMessage && errorMessage.length > 0) {
            onChangeError(errorMessage)
          }
        })
      }} style={styles.button}>
        Sign in
      </Button>
    </View>
  );
};

const AuthenticationSuccessScreen = () => {
  const { signOut } = React.useContext(AuthContext);

  return (
    <View style={styles.content}>
      <Title style={styles.text}>Signed in successfully ðŸŽ‰</Title>
      <Button onPress={signOut} style={styles.button}>
        Sign out
      </Button>
    </View>
  );
};

const SimpleStack = createStackNavigator<AuthStackParams>();

type State = {
  isLoading: boolean;
  isSignout: boolean;
  userToken: null | firebase.User;
};

type Action =
  | { type: 'RESTORE_TOKEN'; token: null | firebase.User }
  | { type: 'SIGN_IN'; token: null | firebase.User }
  | { type: 'SIGN_OUT' };

export default function SimpleStackScreen({
  navigation, route
}: StackScreenProps<ParamListBase>) {
  const passedInParams: any = route.params;
  if (passedInParams) {
    const passedInAction = passedInParams.action;
    React.useEffect(() => {
      dispatch({ type: passedInAction });
    }, []);
  }
  const [state, dispatch] = React.useReducer<React.Reducer<State, Action>>(
    (prevState, action) => {
      switch (action.type) {
        case 'RESTORE_TOKEN':
          return {
            ...prevState,
            userToken: action.token,
            isLoading: false,
          };
        case 'SIGN_IN':
          return {
            ...prevState,
            isSignout: false,
            userToken: action.token,
          };
        case 'SIGN_OUT':
          return {
            ...prevState,
            isSignout: true,
            userToken: null,
          };
      }
    },
    {
      isLoading: true,
      isSignout: false,
      userToken: null,
    }
  );

  React.useEffect(() => {
    dispatch({ type: 'RESTORE_TOKEN', token: state.userToken });
  }, []);

  React.useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, [navigation]);

  const authContext = React.useMemo(
    () => ({
      user: firebase.auth().currentUser,
      signIn: (email: string, password: string) => new Promise<string | void>((success, error) => {
        const provider = new firebase.auth.FacebookAuthProvider();
        try {
          firebase.auth().setPersistence(firebase.auth.Auth.Persistence.LOCAL)
            .then(() => 
              firebase.auth().signInWithEmailAndPassword(email, password).then(user => {
                if (user !== null) {
                  dispatch({ type: 'SIGN_IN', token: user.user })
                  success();
                }
              }).catch(err => error(err))
            ).catch(err => error(err))
        } catch (e) {
          error(e.message);
        }
      }),
      signOut: () => new Promise<void>((s) => dispatch({ type: 'SIGN_OUT' })),
    }),
    []
  );

  if (state.userToken !== null) {
    navigation.popToTop()
  }

  if (state.isLoading) {
    return <SplashScreen />;
  }

  return (
    <AuthContext.Provider value={authContext}>
      <SimpleStack.Navigator
        // take out the back button so as to disable the users ability to bail out of authentication
        screenOptions={{
          headerLeft: undefined
        }}
      >
        {state.userToken === null ? (
          <SimpleStack.Screen
            name="SignIn"
            options={{
              title: 'Sign in',
              animationTypeForReplace: state.isSignout ? 'pop' : 'push',
            }}
            component={SignInScreen}
          />
        ) : (
          <SimpleStack.Screen
            name="Home"
            options={{ title: 'Authentication Success' }}
            component={AuthenticationSuccessScreen}
          />
        )}
      </SimpleStack.Navigator>
    </AuthContext.Provider>
  );
}

const styles = StyleSheet.create({
  content: {
    flex: 1,
    padding: 16,
    justifyContent: 'center',
  },
  input: {
    margin: 8,
    padding: 10,
    borderRadius: 3,
    borderWidth: StyleSheet.hairlineWidth,
    borderColor: 'rgba(0, 0, 0, 0.08)',
  },
  button: {
    margin: 8,
  },
  text: {
    textAlign: 'center',
    margin: 8,
  },
});
