import * as React from 'react';
import { Provider as ReduxProvider } from 'react-redux'
import { createStore } from 'redux'
import reducer from '../reducers'
import middleware from '../middleware'
import {
  ScrollView,
  YellowBox,
  Platform,
  StatusBar,
  I18nManager,
  Dimensions,
  ScaledSize,
  Linking,
} from 'react-native';
// eslint-disable-next-line import/no-unresolved
import { enableScreens } from 'react-native-screens';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import {
  Provider as PaperProvider,
  DefaultTheme as PaperLightTheme,
  DarkTheme as PaperDarkTheme,
  Appbar,
  List,
  Divider,
  Text,
} from 'react-native-paper';
import {
  InitialState,
  NavigationContainer,
  DefaultTheme,
  DarkTheme,
  PathConfigMap,
  NavigationContainerRef,
} from '@react-navigation/native';
import {
  createDrawerNavigator,
  DrawerScreenProps,
} from '@react-navigation/drawer';
import {
  createStackNavigator,
  StackScreenProps,
  HeaderStyleInterpolators,
} from '@react-navigation/stack';
import { useReduxDevToolsExtension } from '@react-navigation/devtools';

import { restartApp } from './Restart';
import AsyncStorage from './AsyncStorage';
import LinkingPrefixes from './LinkingPrefixes';
import SettingsItem from './shared/SettingsItem';
import SimpleStack from './screens/SimpleStack';
import ModalPresentationStack from './screens/ModalPresentationStack';
import StackTransparent from './screens/StackTransparent';
import StackHeaderCustomization from './screens/StackHeaderCustomization';
import MaterialBottomTabs from './screens/MaterialBottomTabs';
import NotFound from './screens/NotFound';
import MasterDetail from './screens/MasterDetail';
import AuthFlow from './screens/AuthFlow';
import LinkComponent from './screens/LinkComponent';

import AuthContext from './shared/AuthContext'

import * as firebase from 'firebase';
import * as Analytics from 'expo-firebase-analytics';

const firebaseConfig = {
  apiKey: "AIzaSyAc7X0OaRKdN50CMAVQDu-EPrUBXhP58n4",
  authDomain: "ineffectua.firebaseapp.com",
  databaseURL: "https://ineffectua.firebaseio.com",
  projectId: "ineffectua",
  storageBucket: "ineffectua.appspot.com",
  messagingSenderId: "sender-id",
  appId: "1:616783557128:android:c473fc0abeb11b16",
  measurementId: "G-153557660"
};

firebase.initializeApp(firebaseConfig);

const store = createStore(reducer, middleware);

YellowBox.ignoreWarnings(['Require cycle:', 'Warning: Async Storage']);

enableScreens();

type RootDrawerParamList = {
  Root: undefined;
  Another: undefined;
};

type RootStackParamList = {
  Home: undefined;
  NotFound: undefined;
} & {
  [P in keyof typeof SCREENS]: undefined;
};

const SCREENS = {
  SimpleStack: { title: 'Simple Stack', component: SimpleStack },
  ModalPresentationStack: {
    title: 'Modal Presentation Stack',
    component: ModalPresentationStack,
  },
  StackTransparent: {
    title: 'Transparent Stack',
    component: StackTransparent,
  },
  StackHeaderCustomization: {
    title: 'Header Customization in Stack',
    component: StackHeaderCustomization,
  },
  MaterialBottomTabs: {
    title: 'Material Bottom Tabs',
    component: MaterialBottomTabs,
  },
  MasterDetail: {
    title: 'Master Detail',
    component: MasterDetail,
  },
  AuthFlow: {
    title: 'Auth Flow',
    component: AuthFlow,
  },
  LinkComponent: {
    title: '<Link />',
    component: LinkComponent,
  },
};

const Drawer = createDrawerNavigator<RootDrawerParamList>();
const Stack = createStackNavigator<RootStackParamList>();

const NAVIGATION_PERSISTENCE_KEY = 'NAVIGATION_STATE';
const THEME_PERSISTENCE_KEY = 'THEME_TYPE';

export default function App() {
  const [theme, setTheme] = React.useState(DefaultTheme);

  const [isReady, setIsReady] = React.useState(Platform.OS === 'web');
  const [initialState, setInitialState] = React.useState<
    InitialState | undefined
  >();

  //detect auth status so we can redirect accordingly
  const [user, setUser] = React.useState<firebase.UserInfo | null>(null);

  React.useEffect(() => {
    const checkAuthentication = async () => {
      firebase.auth().onAuthStateChanged(userState => {
        if (!userState) {
          // user is not authenticated, so navigate
          navigationRef.current?.navigate('AuthFlow');
        }
      });
    };
    checkAuthentication();
  }, []);

  React.useEffect(() => {
    const restoreState = async () => {
      try {
        const initialUrl = await Linking.getInitialURL();

        if (Platform.OS !== 'web' || initialUrl === null) {
          const savedState = await AsyncStorage.getItem(
            NAVIGATION_PERSISTENCE_KEY
          );

          const state = savedState ? JSON.parse(savedState) : undefined;

          if (state !== undefined) {
            setInitialState(state);
          }
        }
      } finally {
        try {
          const themeName = await AsyncStorage.getItem(THEME_PERSISTENCE_KEY);

          setTheme(themeName === 'dark' ? DarkTheme : DefaultTheme);
        } catch (e) {
          // Ignore
        }

        setIsReady(true);
      }
    };

    restoreState();
  }, []);

  const paperTheme = React.useMemo(() => {
    const t = theme.dark ? PaperDarkTheme : PaperLightTheme;

    return {
      ...t,
      colors: {
        ...t.colors,
        ...theme.colors,
        surface: theme.colors.card,
        accent: theme.dark ? 'rgb(255, 55, 95)' : 'rgb(255, 45, 85)',
      },
    };
  }, [theme.colors, theme.dark]);

  const [dimensions, setDimensions] = React.useState(Dimensions.get('window'));

  React.useEffect(() => {
    const onDimensionsChange = ({ window }: { window: ScaledSize }) => {
      setDimensions(window);
    };

    Dimensions.addEventListener('change', onDimensionsChange);

    return () => Dimensions.removeEventListener('change', onDimensionsChange);
  }, []);

  const navigationRef = React.useRef<NavigationContainerRef>(null);

  useReduxDevToolsExtension(navigationRef);

  if (!isReady) {
    return null;
  }

  const isLargeScreen = dimensions.width >= 1024;

  return (

      <PaperProvider theme={paperTheme}>
        {Platform.OS === 'ios' && (
          <StatusBar barStyle={theme.dark ? 'light-content' : 'dark-content'} />
        )}
        <AuthContext.Provider 
          value={{ 
              authenticated: user !== null, 
              signIn: () => navigationRef.current?.navigate('AuthFlow'),
              signOut: () => firebase.auth().signOut(),
          }}
        >
          <NavigationContainer
            ref={navigationRef}
            initialState={initialState}
            onStateChange={(state) => {
              AsyncStorage.setItem(
                NAVIGATION_PERSISTENCE_KEY,
                JSON.stringify(state)
              )
              Analytics.logEvent('screen_change', {
                screen: state?.key
              })
            }}
            onReady={() => {  
              // Get the user, if authenticated
              if (!user) {
                // user is not authenticated, so navigate
                navigationRef.current?.navigate('AuthFlow');
              }
            }}
            theme={theme}
            linking={{
              // To test deep linking on, run the following in the Terminal:
              // Android: adb shell am start -a android.intent.action.VIEW -d "exp://127.0.0.1:19000/--/simple-stack"
              // iOS: xcrun simctl openurl booted exp://127.0.0.1:19000/--/simple-stack
              // Android (bare): adb shell am start -a android.intent.action.VIEW -d "rne://127.0.0.1:19000/--/simple-stack"
              // iOS (bare): xcrun simctl openurl booted rne://127.0.0.1:19000/--/simple-stack
              // The first segment of the link is the the scheme + host (returned by `Linking.makeUrl`)
              prefixes: LinkingPrefixes,
              config: {
                screens: {
                  Root: {
                    path: '',
                    initialRouteName: 'Home',
                    screens: Object.keys(SCREENS).reduce<PathConfigMap>(
                      (acc, name) => {
                        // Convert screen names such as SimpleStack to kebab case (simple-stack)
                        const path = name
                          .replace(/([A-Z]+)/g, '-$1')
                          .replace(/^-/, '')
                          .toLowerCase();

                        acc[name] = {
                          path,
                          screens: {
                            Article: {
                              path: 'article/:author?',
                              parse: {
                                author: (author) =>
                                  author.charAt(0).toUpperCase() +
                                  author.slice(1).replace(/-/g, ' '),
                              },
                              stringify: {
                                author: (author: string) =>
                                  author.toLowerCase().replace(/\s/g, '-'),
                              },
                            },
                            Albums: 'music',
                            Contacts: 'people',
                            NewsFeed: 'feed',
                            Dialog: 'dialog',
                          },
                        };

                        return acc;
                      },
                      {
                        Home: '',
                        NotFound: '*',
                      }
                    ),
                  },
                },
              },
            }}
            fallback={<Text>Loading…</Text>}
            documentTitle={{
              formatter: (options, route) =>
                `${options?.title ?? route?.name} - ineffectua`,
            }}
          >
            <Drawer.Navigator drawerType={isLargeScreen ? 'permanent' : undefined}>
              <Drawer.Screen
                name="Root"
                options={{
                  title: 'Examples',
                  drawerIcon: ({ size, color }) => (
                    <MaterialIcons size={size} color={color} name="folder" />
                  ),
                }}
              >
                {({ navigation }: DrawerScreenProps<RootDrawerParamList>) => (
                  <Stack.Navigator
                    screenOptions={{
                      headerStyleInterpolator: HeaderStyleInterpolators.forUIKit,
                    }}
                  >
                    <Stack.Screen
                      name="Home"
                      options={{
                        title: 'Examples',
                        headerLeft: isLargeScreen
                          ? undefined
                          : () => (
                              <Appbar.Action
                                color={theme.colors.text}
                                icon="menu"
                                onPress={() => navigation.toggleDrawer()}
                              />
                            ),
                      }}
                    >
                      {({ navigation }: StackScreenProps<RootStackParamList>) => (
                        <ScrollView
                          style={{ backgroundColor: theme.colors.background }}
                        >
                          <SettingsItem
                            label="Dark theme"
                            value={theme.dark}
                            onValueChange={() => {
                              AsyncStorage.setItem(
                                THEME_PERSISTENCE_KEY,
                                theme.dark ? 'light' : 'dark'
                              );

                              setTheme((t) => (t.dark ? DefaultTheme : DarkTheme));
                            }}
                          />
                          <Divider />
                          {(Object.keys(SCREENS) as (keyof typeof SCREENS)[]).map(
                            (name) => (
                              <List.Item
                                key={name}
                                testID={name}
                                title={SCREENS[name].title}
                                onPress={() => navigation.navigate(name)}
                              />
                            )
                          )}
                        </ScrollView>
                      )}
                    </Stack.Screen>
                    <Stack.Screen
                      name="NotFound"
                      component={NotFound}
                      options={{ title: 'Oops!' }}
                    />
                    {(Object.keys(SCREENS) as (keyof typeof SCREENS)[]).map(
                      (name) => (
                        <Stack.Screen
                          key={name}
                          name={name}
                          getComponent={() => SCREENS[name].component}
                          options={{ title: SCREENS[name].title }}
                        />
                      )
                    )}
                  </Stack.Navigator>
                )}
              </Drawer.Screen>
            </Drawer.Navigator>
            <Drawer.Navigator drawerType={isLargeScreen ? 'permanent' : undefined}>
              <Drawer.Screen
                name="Root"
                options={{
                  title: 'Examples',
                  drawerIcon: ({ size, color }) => (
                    <MaterialIcons size={size} color={color} name="folder" />
                  ),
                }}
              >
                {({ navigation }: DrawerScreenProps<RootDrawerParamList>) => (
                  <Stack.Navigator
                    screenOptions={{
                      headerStyleInterpolator: HeaderStyleInterpolators.forUIKit,
                    }}
                  >
                    <Stack.Screen
                      name="Home"
                      options={{
                        title: 'Examples',
                        headerLeft: isLargeScreen
                          ? undefined
                          : () => (
                              <Appbar.Action
                                color={theme.colors.text}
                                icon="menu"
                                onPress={() => navigation.toggleDrawer()}
                              />
                            ),
                      }}
                    >
                      {({ navigation }: StackScreenProps<RootStackParamList>) => (
                        <ScrollView
                          style={{ backgroundColor: theme.colors.background }}
                        >
                          <Divider />
                          <SettingsItem
                            label="Dark theme"
                            value={theme.dark}
                            onValueChange={() => {
                              AsyncStorage.setItem(
                                THEME_PERSISTENCE_KEY,
                                theme.dark ? 'light' : 'dark'
                              );

                              setTheme((t) => (t.dark ? DefaultTheme : DarkTheme));
                            }}
                          />
                          <Divider />
                          {(Object.keys(SCREENS) as (keyof typeof SCREENS)[]).map(
                            (name) => (
                              <List.Item
                                key={name}
                                testID={name}
                                title={SCREENS[name].title}
                                onPress={() => navigation.navigate(name)}
                              />
                            )
                          )}
                        </ScrollView>
                      )}
                    </Stack.Screen>
                    <Stack.Screen
                      name="NotFound"
                      component={NotFound}
                      options={{ title: 'Oops!' }}
                    />
                    {(Object.keys(SCREENS) as (keyof typeof SCREENS)[]).map(
                      (name) => (
                        <Stack.Screen
                          key={name}
                          name={name}
                          getComponent={() => SCREENS[name].component}
                          options={{ title: SCREENS[name].title }}
                        />
                      )
                    )}
                  </Stack.Navigator>
                )}
              </Drawer.Screen>
            </Drawer.Navigator>
          </NavigationContainer>
        </AuthContext.Provider>
      </PaperProvider>

  );
}
