import * as React from 'react';
import { ScrollView, StyleSheet } from 'react-native';
import { Subheading, Avatar, Button, Divider } from 'react-native-paper';

import * as firebase from 'firebase';
import { DrawerContentComponentProps, DrawerContentOptions } from '@react-navigation/drawer';
import { DefaultTheme, DarkTheme, Theme } from '@react-navigation/native';
import SettingsItem from '../shared/SettingsItem';
import AsyncStorage from '../AsyncStorage';
import { THEME_PERSISTENCE_KEY } from '../../Types';

export default function Profile({ navigation }: DrawerContentComponentProps<DrawerContentOptions>, user: firebase.User | null, theme: Theme, setTheme: React.Dispatch<React.SetStateAction<Theme>>) {
  const thumbnail = user && user.photoURL ? require(user.photoURL) : null;

  return (
    <ScrollView style={styles.content} >
      {thumbnail ? (<Avatar.Image source={thumbnail} style={styles.image} />) : null}
      <Subheading>Hi{user?.displayName ? ' ' + user?.displayName : null}!</Subheading>
      <SettingsItem
        label="Dark theme"
        value={theme.dark}
        onValueChange={() => {
          AsyncStorage.setItem(
            THEME_PERSISTENCE_KEY,
            theme.dark ? 'light' : 'dark'
          );

          setTheme((t) => (t.dark ? DefaultTheme : DarkTheme));
        }}
      />
      <Divider />
      <Button onPress={() => navigation.navigate('AuthFlow', {action: 'SIGN_OUT'})} style={styles.button}>
        Sign Out
      </Button>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  content: {
    flexDirection: 'column',
    paddingHorizontal: 16,
    paddingVertical: 12,
  },
  button: {
    margin: 8,
  },
  text: {
    textAlign: 'center',
    margin: 8,
  },
  image: {
    alignItems: 'center'
  }
});
